import './App.css';
import './normal.css';
import { useState } from 'react';

function App() {

const [input, setInput] = useState("");
const [chatLog, setChatLog] = useState([{
  user: "gpt",
  message: "henLo, what is thine request?"
},{
  user: "me",
  message: "...‰∑Ä"
}]);

  async function handleSubmit(e){
    e.preventDefault();
    setChatLog([...chatLog, { user: 'me', message: `${input}`}])
    setInput =("");
  }

  return (
    <div className="App">
      <aside className="sidemenu">
      <div className="side-menu-button">
        <span>ùåπ</span>
        New Chat
      </div>
    </aside>
    <section className="chatbox">
    <div class="chat-log">
     {chatLog.map((message, index) => (
        <ChatMessage key={index} message={message} />
      ))}

</div>

<div className="chat-input-holder">
  <form onSubmit={handleSubmit}>
      <input
      rows="1"
      value={input}
      onChange={(e)=> setInput(e.target.value)}
      className="chat-input-textarea">



      </input>
      </form>
    </div>
    <button class="send-button">‰∑Ä</button>        
    </section>
    </div>
  );
}

const ChatMessage = ({ message }) => {
  return (
    <div className= {`chat-message ${message.user === "gpt" && "chatgpt"}`}>
          <div className="chat-message-center">
            <div className={`avatar ${message.user === "gpt" && "chatgpt"}`}>
              {message.user=== "gpt" && <div class="chatgpt-symbol">‚óé</div>}
              {/* {message.user=== "me" && <div class="me-symbol">üÅ¢</div>} */}
              <div className={`avatar ${message.user === "me" && "me"}`}>
              {message.user=== "me" && <div class="me-symbol">‚ù¨‚ù≠</div>}
              </div>
            </div>
            <div className="message">
              {message.message}
            </div>
          </div>
        </div>
  )
}
export default App;
